import React, { useState } from 'react';

const Form = (props) => {
  console.log(props)
  return (<div>
    {props.formData.map(data => {
      if(data.type === "input"){
        const style = {
          width: (data.width === "half") ? "45%" : (data.width === "full") ? "100%" : null,
          marginLeft: (data.width === "half") ? "5%" : null
        }
        return (<input style={style} />)
      }
    })}
  </div>);
}

