import React, { useState } from 'react';

const Form = (props) => {
  console.log(props)
  return (<div>
    {props.formData.map(data => {
      if(data.type === "input"){
        const style = {
          width: (data.width === "half") ? "45%" : (data.width === "full") ? "93%" : null,
          marginLeft: "2.5%",
          marginTop: 10,
          border: "none",
          fontSize: 20
        }
        return (<div style={{width: "100%"}}>
          <input style={style} />
        </div>)
      }
    })}
  </div>);
}

