import React, { useState } from 'react';

const Form = (props) => {
  console.log(props)
  return (<div>
    {props.formData.map(data => {
      if(data.type === "input"){
        const style = {
          width: (data.width === "half") ? "45%" : (data.width === "full") ? "90%" : null,
          marginLeft: "2.5%",
          border: "none",
          fontSize: 20
        }
        return (<input style={style} />)
      }
    })}
  </div>);
}

const styles = {
}

export default Form;
