import React, { useState } from 'react';

const Form = (props) => {
  console.log(props)
  return (<div style={{
    width: "100%",
    display: "flex"
  }}>
    {props.formData.map(data => {
      if(data.type === "input"){
        const styles = {
          input: {
            width: (data.width === "half") ? "45%" : (data.width === "full") ? "93%" : null,
            border: "none",
            fontSize: 20
          }
        }
        return (<div style={{
          display: "inline",
          marginLeft: "2.5%",
          marginTop: 10,
          display: "flex",
          flexDirection: "column"
        }}>
          <span>{data.label}</span>
          <input style={styles.input} />
        </div>)
      }
    })}
  </div>);
}

const styles = {
}

export default Form;
